import React, { useState } from "react";
import axios from "axios";
import "./App.css";
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from "recharts";
import "bootstrap/dist/css/bootstrap.min.css";

import { API } from "./api";

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const [loading, setLoading] = useState(false);

  const handleSend = async () => {
    if (!input.trim()) return;

    setMessages((prev) => [...prev, { sender: "user", text: input }]);
    const userMessage = input;
    setInput("");
    setLoading(true);

    try {
      const response = await API.post("/query/", { message: userMessage });

      const botMsg = response.data?.summary || "No summary available.";
      setMessages((prev) => [...prev, { sender: "bot", text: botMsg, data: response.data }]);
    } catch (err) {
      setMessages((prev) => [
        ...prev,
        { sender: "bot", text: "Something went wrong while processing your request." }
      ]);
    }

    setLoading(false);
  };

  const renderInsights = (data) => {
    if (!data) return null;

    return (
      <div className="insight-card">
        <div className="insight-header">üìä Insights</div>
        <div className="insight-text">
          <b>Area:</b> {data.areas?.join(", ")} <br />
          <b>Years:</b> {data.years?.join(" - ")} <br />
          <b>Metric:</b> {data.metric} <br />
        </div>
      </div>
    );
  };

  const renderChart = (data) => {
    if (!data || !data.chart) return null;

    const chartData = data.chart;

    return (
      <div className="chart-container">
        <LineChart width={750} height={350} data={chartData}>
          <CartesianGrid strokeDasharray="3 3" />
          <XAxis dataKey="year" />
          <YAxis />
          <Tooltip />
          <Legend />
          {Object.keys(chartData[0] || {})
            .filter((key) => key !== "year")
            .map((metric, index) => (
              <Line
                key={index}
                type="monotone"
                dataKey={metric}
                strokeWidth={3}
                dot={{ r: 3 }}
              />
            ))}
        </LineChart>
      </div>
    );
  };

  const renderTable = (data) => {
    if (!data || !data.table || data.table.length === 0) return null;

    const tableData = data.table;
    const columns = Object.keys(tableData[0]);

    return (
      <div className="table-wrapper">
        <table className="table table-bordered table-striped">
          <thead>
            <tr>
              {columns.map((col, idx) => (
                <th key={idx}>{col.toUpperCase()}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {tableData.map((row, idx) => (
              <tr key={idx}>
                {columns.map((col, cidx) => (
                  <td key={cidx}>{row[col]}</td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="container-main">
      <h2 className="text-center mb-4">üèô Real Estate Analytics Chatbot</h2>

      <div className="chat-box">
        {messages.map((msg, idx) => (
          <div key={idx} className={`message ${msg.sender === "user" ? "message-user" : "message-bot"}`}>
            <div className="message-bubble">{msg.text}</div>
          </div>
        ))}

        {loading && (
          <div className="message message-bot">
            <div className="message-bubble">Analyzing data...</div>
          </div>
        )}
      </div>

      {/* Render components for last bot message */}
      {messages
        .filter((m) => m.sender === "bot" && m.data)
        .slice(-1)
        .map((msg, idx) => (
          <React.Fragment key={idx}>
            {renderInsights(msg.data)}
            {renderChart(msg.data)}
            {renderTable(msg.data)}
          </React.Fragment>
        ))}

      {/* INPUT */}
      <div className="input-box">
        <input
          placeholder="Ask about any area... (e.g., 'Analyze Wakad')"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={(e) => e.key === "Enter" && handleSend()}
        />
        <button className="send-btn" onClick={handleSend}>
          Send
        </button>
      </div>
    </div>
  );
}

export default App;
