import React, { useState } from "react";
import api from "./api";
import {
  Container,
  Row,
  Col,
  Card,
  Form,
  Button,
  Table,
  Spinner,
  Badge,
} from "react-bootstrap";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  Tooltip,
  Legend,
  CartesianGrid,
  ResponsiveContainer,
} from "recharts";

function App() {
  const [message, setMessage] = useState("");
  const [history, setHistory] = useState([]);
  const [metric, setMetric] = useState("price");
  const [responseData, setResponseData] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSend = async (e) => {
    e.preventDefault();
    if (!message.trim()) return;

    const userMsg = { from: "user", text: message };
    setHistory((prev) => [...prev, userMsg]);
    setLoading(true);

    try {
      const res = await api.post("/query/", {
        message,
        metric,
      });
      const data = res.data;
      setResponseData(data);

      setHistory((prev) => [
        ...prev,
        { from: "bot", text: data.summary || "No summary available." },
      ]);
    } catch (err) {
      console.error(err);
      setHistory((prev) => [
        ...prev,
        {
          from: "bot",
          text: "Something went wrong while processing your request.",
        },
      ]);
    } finally {
      setLoading(false);
      setMessage("");
    }
  };

  const chartData =
  responseData && responseData.chart
    ? responseData.chart.labels.map((year, idx) => {
        const point = { year };
        responseData.chart.datasets.forEach((ds) => {
          point[ds.label] = ds.data[idx] ?? null;
        });
        return point;
      })
    : [];


  const handleDownloadCsv = () => {
    if (!responseData) return;
    const areas = responseData.areas || [];
    const yr = responseData.year_range || [];
    const params = new URLSearchParams();
    if (areas.length) params.set("areas", areas.join(","));
    if (yr && yr.length === 2) {
      params.set("start_year", yr[0]);
      params.set("end_year", yr[1]);
    }
    const url = `http://localhost:8000/api/download_csv/?${params.toString()}`;
    window.open(url, "_blank");
  };

  const handleDownloadPdf = () => {
    if (!responseData) return;
    const areas = responseData.areas || [];
    const yr = responseData.year_range || [];
    const params = new URLSearchParams();
    if (areas.length) params.set("areas", areas.join(","));
    if (yr && yr.length === 2) {
      params.set("start_year", yr[0]);
      params.set("end_year", yr[1]);
    }
    params.set("metric", metric);
    const url = `http://localhost:8000/api/report_pdf/?${params.toString()}`;
    window.open(url, "_blank");
  };

  const insights = responseData?.insights;

  return (
    <Container
      fluid
      className="py-4"
      style={{ background: "#f5f7fb", minHeight: "100vh" }}
    >
      <Row className="mb-4">
        <Col>
          <h2 className="text-center">
            üè° Real Estate Analysis Chatbot
          </h2>
          <p className="text-center text-muted">
            Ask things like:{" "}
            <em>"Analyze Wakad"</em>,{" "}
            <em>"Compare Baner and Balewadi from 2015 to 2020"</em>,{" "}
            <em>"Show price and demand trend for Aundh"</em>.
          </p>
        </Col>
      </Row>

      <Row>
        {/* Chat panel */}
        <Col md={4} className="mb-3">
          <Card className="h-100 shadow-sm">
            <Card.Header className="d-flex justify-content-between align-items-center">
              <span>Chat</span>
              <Form.Select
                size="sm"
                style={{ width: "45%" }}
                value={metric}
                onChange={(e) => setMetric(e.target.value)}
              >
                <option value="price">Price</option>
                <option value="demand">Demand</option>
                <option value="both">Price + Demand</option>
              </Form.Select>
            </Card.Header>
            <Card.Body className="d-flex flex-column">
              <div
                style={{
                  flex: 1,
                  overflowY: "auto",
                  border: "1px solid #eee",
                  borderRadius: "6px",
                  padding: "0.5rem",
                  marginBottom: "0.75rem",
                  maxHeight: "360px",
                  background: "white",
                }}
              >
                {history.length === 0 && (
                  <div className="text-muted small">
                    Start by asking: <br />
                    ‚Ä¢ "Analyze Wakad" <br />
                    ‚Ä¢ "Compare Baner and Balewadi" <br />
                    ‚Ä¢ "Show price and demand for Aundh between 2012 and 2018"
                  </div>
                )}
                {history.map((msg, idx) => (
                  <div
                    key={idx}
                    className={`mb-2 ${
                      msg.from === "user" ? "text-end" : "text-start"
                    }`}
                  >
                    <Badge
                      bg={msg.from === "user" ? "primary" : "secondary"}
                      className="mb-1"
                    >
                      {msg.from === "user" ? "You" : "Bot"}
                    </Badge>
                    <div
                      style={{
                        display: "inline-block",
                        padding: "0.35rem 0.6rem",
                        borderRadius: "12px",
                        background:
                          msg.from === "user" ? "#e3f2fd" : "#f1f3f5",
                        maxWidth: "100%",
                        whiteSpace: "pre-wrap",
                      }}
                    >
                      {msg.text}
                    </div>
                  </div>
                ))}
                {loading && (
                  <div className="d-flex align-items-center gap-2 mt-2">
                    <Spinner animation="border" size="sm" />
                    <span className="text-muted small">Thinking...</span>
                  </div>
                )}
              </div>

              <Form onSubmit={handleSend}>
                <Form.Control
                  type="text"
                  placeholder='Try: "Compare Wakad and Baner from 2015 to 2020"'
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  className="mb-2"
                />
                <Button
                  type="submit"
                  className="w-100"
                  disabled={loading || !message.trim()}
                >
                  Send
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Col>

        {/* Analysis panel */}
        <Col md={8} className="mb-3">
          {responseData ? (
            <>
              {/* Insight cards */}
              {insights && insights.areas && Object.keys(insights.areas).length > 0 && (
                <Card className="mb-3 shadow-sm">
                  <Card.Header>Key Insights & Recommendations</Card.Header>
                  <Card.Body>
                    {insights.ranked_areas?.length > 0 && (
                      <p className="mb-3 small">
                        <strong>Best overall investment (within this query):</strong>{" "}
                        {insights.ranked_areas[0].area}{" "}
                        ({insights.ranked_areas[0].investment_score}/10)
                      </p>
                    )}
                    <Row>
                      {Object.entries(insights.areas).map(([area, s]) => (
                        <Col md={6} key={area} className="mb-3">
                          <Card
                            className="h-100 border-0"
                            style={{ background: "#f8fafc" }}
                          >
                            <Card.Body>
                              <h6 className="mb-1">{area}</h6>
                              <div className="small text-muted mb-2">
                                Period: {s.year_start}‚Äì{s.year_end} ‚Ä¢ Demand:{" "}
                                {s.demand_trend}
                              </div>
                              <div className="small mb-1">
                                <div>
                                  Avg price: ‚Çπ
                                  {s.avg_price ? Math.round(s.avg_price).toLocaleString("en-IN") : "N/A"}
                                </div>
                                <div>
                                  Price CAGR:{" "}
                                  {(s.price_cagr * 100).toFixed(1) || "N/A"}

                                </div>
                                {s.price_forecast_next_year && (
                                  <div>
                                    Next year est.: ‚Çπ
                                    {Math.round(
                                      s.price_forecast_next_year
                                    ).toLocaleString("en-IN")}
                                  </div>
                                )}
                              </div>
                              <div className="small text-muted mt-2">
                                Investment score:{" "}
                                {s.investment_score.toFixed(1)}/10 ‚Ä¢ Growth{" "}
                                {s.growth_score.toFixed(1)}/10 ‚Ä¢ Demand{" "}
                                {s.demand_score.toFixed(1)}/10 ‚Ä¢ Risk{" "}
                                {s.risk_score.toFixed(1)}/10
                              </div>
                            </Card.Body>
                          </Card>
                        </Col>
                      ))}
                    </Row>
                  </Card.Body>
                </Card>
              )}

              <Card className="mb-3 shadow-sm">
                <Card.Header className="d-flex justify-content-between align-items-center">
                  <span>Trends</span>
                  <div className="d-flex gap-2">
                    <Button
                      variant="outline-secondary"
                      size="sm"
                      onClick={handleDownloadCsv}
                    >
                      Download CSV
                    </Button>
                    <Button
                      variant="outline-secondary"
                      size="sm"
                      onClick={handleDownloadPdf}
                    >
                      Download PDF Report
                    </Button>
                  </div>
                </Card.Header>
                <Card.Body style={{ height: 320 }}>
                  {chartData.length ? (
                    <ResponsiveContainer width="100%" height="100%">
                      <LineChart data={chartData}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="year" />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        {responseData.chart.datasets.map((ds) => (
                          <Line
                            key={ds.label}
                            type="monotone"
                            dataKey={ds.label}
                            dot={false}
                            strokeWidth={2}
                          />
                        ))}
                      </LineChart>
                    </ResponsiveContainer>
                  ) : (
                    <div className="text-muted">
                      No chart data available for this query.
                    </div>
                  )}
                </Card.Body>
              </Card>

              <Card className="shadow-sm">
                <Card.Header>Filtered Data</Card.Header>
                <Card.Body style={{ maxHeight: 320, overflowY: "auto" }}>
                  {responseData.table && responseData.table.length > 0 ? (
                    <Table striped bordered hover size="sm" responsive>
                      <thead>
                        <tr>
                          {Object.keys(responseData.table[0]).map((key) => (
                            <th key={key}>{key}</th>
                          ))}
                        </tr>
                      </thead>
                      <tbody>
                        {responseData.table.map((row, idx) => (
                          <tr key={idx}>
                            {Object.keys(row).map((key) => (
                              <td key={key}>{row[key]}</td>
                            ))}
                          </tr>
                        ))}
                      </tbody>
                    </Table>
                  ) : (
                    <div className="text-muted">
                      No rows found for this query.
                    </div>
                  )}
                </Card.Body>
              </Card>
            </>
          ) : (
            <Card className="shadow-sm">
              <Card.Body>
                <h5>Welcome üëã</h5>
                <p className="text-muted mb-1">
                  Use the chat on the left to ask for analysis of localities,
                  price trends, demand patterns, and year ranges.
                </p>
                <ul className="text-muted small mb-0">
                  <li>‚ÄúAnalyze Wakad‚Äù</li>
                  <li>‚ÄúCompare Baner and Balewadi‚Äù</li>
                  <li>
                    ‚ÄúShow price and demand for Aundh between 2012 and 2018‚Äù
                  </li>
                  <li>
                    ‚ÄúWhich looks better for investment: Wakad or Baner between
                    2015‚Äì2020?‚Äù
                  </li>
                </ul>
              </Card.Body>
            </Card>
          )}
        </Col>
      </Row>
    </Container>
  );
}

export default App;
